import React from "react";
import Context from "./Context";

const useContext = WrappedComponent => {
    const WithHOC = props => {
        return (
            <Context.Consumer>
                {context => <WrappedComponent {...props} context={context} />}
            </Context.Consumer>
        );
    };

    return WithHOC;
};

export default useContext;
