import React from "react";
import withContext from "./withContext";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        margin: 'auto',
        maxWidth: 500,
    },
    image: {
        width: 128,
        height: 128,
    },
    img: {
        margin: 'auto',
        display: 'block',
        maxWidth: '100%',
        maxHeight: '100%',
    },
}));


const CartItem = props => {
    const { cartItem, itemKey } = props;
    const { products } = props.context;
    const classes = useStyles();
    // console.log(cartItem);
    return (
        <div>



            <Paper className={classes.paper}>
                <Grid container spacing={2}>
                    <Grid item>
                        <ButtonBase className={classes.image}>
                            <img className={classes.img} alt="complex" src="https://lh3.googleusercontent.com/proxy/2q5UPHnj-OsCirJUjyUhEbRTVwQkApDvHcc-iZ99smDjTMMXzkSlFA8LW8pDfd0T6_V7mFdRR-E8_p4V6hhq6BlmgiSfm9PPRehi8t1c42QthFOmFcltqxecmzhrqmqEE3oFZkQ" />
                        </ButtonBase>
                    </Grid>
                    <Grid item xs={12} sm container>
                        <Grid item xs container direction="column" spacing={2}>
                            <Grid item xs>
                                <Typography gutterBottom variant="subtitle1">
                                    {cartItem.product.name}
                                </Typography>
                                <Typography variant="body2" gutterBottom>
                                    {cartItem.product.price}
                                </Typography>
                                <Typography variant="body2" color="textSecondary">
                                    {cartItem.quantity}
                                </Typography>
                            </Grid>
                            <Grid item>
                                <button
                                    onClick={() =>
                                        props.addItem({
                                            id: cartItem.product.name,
                                            products,
                                            quantity: 1
                                        })
                                    }
                                >
                                    <ExpandLessIcon />
                                </button>
                            </Grid>
                        </Grid>
                        <Grid item>
                            <button
                                onClick={() =>
                                    props.removeItem({
                                        id: cartItem.product.name,
                                        products,
                                        quantity: 1
                                    })
                                }
                            >
                                delete
              </button>
                        </Grid>
                    </Grid>
                </Grid>
            </Paper>



            {/* <div>
                {cartItem.product.name}
