            <button
                onClick={() =>
                    props.addItem({
                        id: cartItem.product.name,
                        products,
                        quantity: 1
                    })
                }
            >
                add
              </button>
            <button
                onClick={() =>
                    props.removeItem({
                        id: cartItem.product.name,
                        products,
                        quantity: 1
                    })
                }
            >
                delete
              </button>
        </div > */}
